vec4 Effect2(vec2 uv) {
    // Scale and animate UV coordinates
    vec2 st = uv * 10.0 + iTime;

    // Create a grid of points with sine wave modulation
    float x = sin(st.x * 0.5 + iTime) * 0.5 + 0.5;
    float y = sin(st.y * 0.5 + iTime + PI/2.) * 0.5 + 0.5;

    // Combine coordinates and add turbulence
    vec3 color = hsl2rgb(vec3(
        (x + y) * 0.5,   // Hue based on position
        1.0 - turbulent(st.xyy * 0.5), // Saturation with turbulence
        0.8 + sin(iTime) * 0.2 // Brightness variation over time
    ));

    return vec4(color, 1.0);
}
