vec4 Effect2(vec2 uv) {
    // Distort UV coordinates with sine waves
    vec2 distortedUV = uv + 0.3 * sin(5.0 * uv + iTime);

    // Create a grid pattern using mod
    vec2 grid = mod(distortedUV * 10.0, 1.0);

    // Add rotation based on time and position
    float angle = iTime * 0.2 + length(grid - 0.5) * 2.0;
    mat2 rotMatrix = mat2(cos(angle), -sin(angle),
                         sin(angle), cos(angle));
    vec2 rotatedGrid = (grid - 0.5) * rotMatrix + 0.5;

    // Create color variations using HSL
    vec3 hslColor = vec3(
        mod(length(rotatedGrid - 0.5) * 10.0 + iTime, 1.0),
        0.7,
        0.6 + 0.4 * sin(iTime)
    );

    // Convert to RGB and add turbulence effect
    vec3 rgbColor = hsl2rgb(hslColor);
    float turb = turbulent(vec3(rotatedGrid, iTime));
    rgbColor *= 1.0 + 0.5 * turb;

    return vec4(rgbColor, 1.0);
}
