vec4 Effect2(vec2 uv) {
    // Create grid of circles
    vec2 st = uv * 10.0; // Scale up for more circles
    vec2 grid = floor(st);

    // Calculate distance from center
    vec2 offset = fract(st) - 0.5;
    float dist = length(offset);

    // Time-based expansion
    float time = iTime * 0.1;
    float radius = 0.3 + noise(vec3(grid, time)) * 0.2;

    // Check if inside circle
    float alpha = 0.0;
    if (dist < radius) {
        // Calculate color with rainbow hues
        float hue = dist * 6.0 + time * 6.0; // Rainbow effect
        vec3 color = hsl2rgb(vec3(hue, 1.0, 0.5));

        // Add noise for texture
        color *= 1.0 + noise(vec3(uv * 100.0, time)) * 0.5;

        // Calculate alpha based on distance and time
        float t = smoothstep(0.0, 1.0, (time - dist) / 2.0);
        alpha = t;

        return vec4(color, alpha);
    }

    return vec4(0.0);
}
