vec4 Effect2(vec2 uv) {
    // Scale UV coordinates for more detailed pattern
    vec2 st = uv * 10.0;

    // Add time-based animation
    float time = iTime * 0.5;

    // Create undulating sine wave patterns
    float waveX = sin(st.x + time) * 0.5 + 0.5;
    float waveY = sin(st.y + time * 0.7) * 0.5 + 0.5;

    // Combine waves with turbulence for more dynamic effect
    vec2 noiseSt = st + vec2(turbulent(vec3(st.x, time, 0.0)), turbulent(vec3(time, st.y, 0.0)));
    float value = sin(noiseSt.x * pi) * cos(noiseSt.y * pi);

    // Create vibrant color shifts using HSL
    vec3 color = hsl2rgb(vec3(
        (waveX + waveY + value) * 360.0,   // Hue variation
        0.8,                                // Saturation
        0.5 + sin(time) * 0.4             // Brightness variation
    ));

    return vec4(color, 1.0);
}
