vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan2(st.y, st.x);
    float r = length(st);

    // Add rotation over time
    theta += iTime * 0.5;

    // Create geometric pattern using sine waves
    float pattern = sin(theta + iTime) * 0.5 + 0.5;
    pattern *= sin(r * 10.0 + iTime);

    // Add noise for shimmering effect
    vec2 noiseUV = uv + vec2(iTime * 0.1, 0.0);
    float noiseValue = noise(noiseUV * 10.0) * 0.5 + 0.5;

    // Create color transitions using HSL
    vec3 baseColor = hsl(theta * 0.5 + 0.5, 0.7, 0.6);
    vec3 secondaryColor = hsl(r * 0.3 + iTime * 0.2, 0.8, 0.7);

    // Mix colors based on pattern and noise
    vec3 finalColor = mix(baseColor, secondaryColor, noiseValue);
    finalColor = hsv2rgb(finalColor);

    // Combine everything for the final effect
    float intensity = pattern * noiseValue;
    return vec4(finalColor * intensity, 1.0);
}
