vec4 Effect2(vec2 uv) {
    // Rotate UV coordinates over time
    float angle = sin(iTime * 0.5) * 0.5 + 0.5;
    vec2 rotatedUv = vec2(
        uv.x * cos(angle) - uv.y * sin(angle),
        uv.x * sin(angle) + uv.y * cos(angle)
    );

    // Create dynamic line pattern using turbulence
    float t = turbulent(rotatedUv * 10.0 + iTime);
    vec3 color = hsl2rgb(vec3(
        mod(turbulent(uv * 5.0 + iTime) * 2.0, 1.0),
        0.7,
        0.6
    ));

    // Add noise interaction
    vec4 noiseSample = texture(iChannel0, rotatedUv * 0.5 + 0.5);
    float finalIntensity = t * (1.0 - length(noiseSample.rgb));

    return vec4(color * finalIntensity, 1.0);
}
