vec4 Effect2(vec2 uv) {
    // Create a position vector with time offset
    vec3 pos = vec3(uv * 2.0 - 1.0, iTime * 0.5);

    // Add some turbulence to create flowing effect
    float t = turbulent(pos * 4.0 + vec3(0.0, 0.0, iTime));
    t += turbulent(pos * 2.0 + vec3(0.0, 0.0, iTime)) * 0.5;

    // Create color transitions using HSL
    vec3 color = hsl(
        sin(iTime * 0.1) * 0.5 + 0.5,
        0.7 + t * 0.3,
        0.6 + abs(sin(iTime)) * 0.2
    );

    // Add noise to create liquid-like patterns
    vec2 st = uv * 4.0;
    float n1 = noise(st);
    float n2 = noise(st + vec2(0.5, 0.5));
    float n3 = noise(st + vec2(1.0, 1.0));

    // Combine noise layers with different weights
    float liquid = (n1 * 0.4) + (n2 * 0.3) + (n3 * 0.2);
    liquid += t * 0.5;

    // Final color transformation
    vec3 finalColor = hsl2rgb(vec3(
        color.r,
        color.g + liquid * 0.3,
        color.b + liquid * 0.4
    ));

    return vec4(finalColor, 1.0);
}
