vec4 Effect2(vec2 uv) {
    // Recursive fractal pattern using sine functions
    float scale = 1.0;
    vec3 color = vec3(0.0);

    for(int i = 0; i < 8; i++) {
    float value = sin(uv.x * scale + iTime) *
    sin(uv.y * scale + iTime) *
    sin((uv.x + uv.y) * scale + iTime);

    if(value > 0.0) {
    color += vec3(1.0, 0.5, 0.2) / pow(2.0, float(i));
    } else {
    color -= vec3(0.2, 0.5, 1.0) / pow(2.0, float(i));
    }

    scale *= 2.0;
    }

    return vec4(abs(color * 2.0), 1.0);
