vec4 Effect2(vec2 uv) {
    // Center coordinates and scale for better pattern distribution
    vec2 st = uv * 2.0 - 1.0;

    // Create rotating polar coordinates
    float theta = atan2(st.y, st.x) + iTime * 0.5;
    float r = length(st);

    // Create gradient with sine wave modulation
    float gradient = sin(r * 8.0 + iTime) * 0.5 + 0.5;
    gradient += sin(theta * 3.0 + iTime * 2.0) * 0.3;

    // Add noise for texture
    vec2 noiseScale = st * 4.0 + iTime;
    float noiseVal = turbulent(noiseScale);

    // Create color transitions using HSL
    vec3 color1 = hsl(theta * 2.0, 0.7, 0.6);
    vec3 color2 = hsl(sin(iTime) * pi + theta, 0.8, 0.5);
    vec3 finalColor = mix(color1, color2, smoothstep(0.0, 1.0, gradient));

    // Add noise influence
    finalColor *= 1.0 + noiseVal * 0.3;

    return vec4(finalColor, 1.0);
}
