vec4 Effect2(vec2 uv) {
    // Create base pattern with layered sine functions
    vec2 st = uv * 5.0 + iTime;
    float base = sin(st.x) * cos(st.y);

    // Add rotating element
    float angle = iTime * 0.5;
    vec2 rotatedUv = uv * mat2(cos(angle), -sin(angle), sin(angle), cos(angle));

    // Layered sine patterns with time offset
    float pattern1 = sin(rotatedUv.x * 4.0 + iTime) * 0.5;
    float pattern2 = cos(rotatedUv.y * 3.0 + iTime * 0.7);
    float combined = (pattern1 + pattern2) * 0.5;

    // Add turbulence for more dynamic effect
    vec3 noisePos = vec3(uv * 4.0, iTime * 0.1);
    float turb = turbulent(noisePos) * 0.5;

    // Combine all elements
    float finalValue = combined + turb;

    // Create color cycling using HSL
    vec3 hslColor = vec3(finalValue * 0.5 + 0.5, 1.0, 0.7);
    vec3 rgbColor = hsl2rgb(hslColor);

    return vec4(rgbColor, 1.0);
}
