vec4 Effect2(vec2 uv) {
    // Create hexagonal grid coordinates
    vec2 q = uv * 5.0;
    vec2 r = vec2(q.x + q.y / 2.0, q.y * sqrt(3.0)/2.0);

    // Calculate hexagon center and distance
    vec2 c = floor(r) + vec2(0.5, 0.5);
    vec2 d = fract(r - c);

    // Create pulsating color pattern
    float time = iTime * 0.5;
    vec3 color = hsl2rgb(vec3(fract(time + c.x + c.y), 1.0, 0.7));

    // Add noise and turbulence for dynamic effect
    float noiseVal = turbulent(r * 0.5 + time);
    color *= 1.0 + sin(time) * 0.2;

    // Create hexagonal shape with gradient
    float dist = length(d - vec2(0.5, 0.5)) / sqrt(3.0)/2.0;
    float alpha = smoothstep(0.49, 0.51, dist);

    return vec4(color * (1.0 + noiseVal * 0.2), alpha);
}
