vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + sin(iTime * 0.5) * 3.0;

    // Create rotating geometric patterns using sine waves
    float pattern = sin(theta * 8.0 + iTime) * 0.5 + 0.5;
    pattern += turbulent(vec2(r * 10.0, theta)) * 0.4;

    // Calculate color components with pulsating hues
    vec3 color;
    color.x = sin(theta * 4.0 + iTime * 2.0) * 0.5 + 0.5;
    color.y = cos(r * 16.0 - iTime * 3.0) * 0.5 + 0.5;
    color.z = pattern * (sin(iTime) * 0.5 + 0.5);

    // Convert to HSL and back to RGB for vibrant colors
    vec3 hslColor = vec3(
        mod(theta * 4.0 + iTime, 2.0),
        1.0,
        sin(r * 8.0 + iTime) * 0.5 + 0.5
    );

    return vec4(hsl2rgb(hslColor), 1.0);
}
