vec4 Effect2(vec2 uv) {
    // Scale UV coordinates and add time component
    vec2 st = uv * 5.0 + iTime;

    // Create a base color using HSL
    vec3 color = hsl(0.5, 1.0, 0.5);

    // Add dynamic noise and turbulence effects
    float t = turbulent(st) * 0.5 + 0.5;
    color = mix(color, hsv2rgb(vec3(turbulent(st * 2.0), 1.0, 1.0)), t);

    // Create a secondary color layer with different parameters
    vec3 color2 = hsl(0.3, 1.0, 0.7);
    float t2 = turbulent(st + 1.0) * 0.5 + 0.5;
    color2 = mix(color2, hsv2rgb(vec3(turbulent(st * 2.0 + 1.0), 1.0, 1.0)), t2);

    // Combine the two colors with some blending
    vec3 finalColor = mix(color, color2, 0.5);

    return vec4(finalColor, 1.0);
}
