vec4 Effect2(vec2 uv) {
    // Scale and offset UV coordinates for grid pattern
    vec2 st = uv * 5.0 + vec2(1.0);

    // Calculate hexagon center positions using sine and cosine patterns
    vec2 pos = vec2(
        sin(st.x * 0.5 + iTime) + cos(st.y * 0.5),
        cos(st.x * 0.5 - iTime) + sin(st.y * 0.5)
    );

    // Add turbulence for dynamic movement
    float turb = turbulent(pos * 0.5 + vec2(iTime, 0.0)) * 0.5 + 0.5;

    // Calculate rotation angle based on time and position
    float angle = sin(iTime * 3.0 + pos.x * 2.0) * 0.5;
    vec2 rotatedPos = vec2(
        cos(angle) * (pos.x - 0.5) - sin(angle) * (pos.y - 0.5),
        sin(angle) * (pos.x - 0.5) + cos(angle) * (pos.y - 0.5)
    ) + 0.5;

    // Create color variations using HSL to RGB conversion
    vec3 color = hsl(
        sin(iTime * 0.2 + rotatedPos.x * 2.0) * 0.5 + 0.5,
        cos(iTime * 0.3 + rotatedPos.y * 2.0) * 0.5 + 0.5,
        0.7
    );

    // Combine turbulence with color for final output
    return vec4(color * turb, 1.0);
}
