vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for rotation effects
    float r = length(uv);
    float a = atan2(uv.y, uv.x);

    // Add time-based rotation
    a += iTime * 0.5;

    // Create pulsating edges using turbulence
    float pulse = turbulent(vec3(r, a, iTime)) * 0.5 + 0.5;

    // Combine polar coordinates with noise for dynamic patterns
    vec2 st = uv * 10.0; // Scale up for more detail
    float pattern = noise(st) * 0.5 + 0.5;

    // Create color transitions based on angle and time
    vec3 color = hsl(a * 0.1, 0.7, 0.6);

    // Add pulsating effect to colors
    color *= pulse;

    // Mix in noise pattern for texture
    color = mix(color, vec3(pattern), 0.5);

    return vec4(color, 1.0);
}
