vec4 Effect2(vec2 uv) {
    // Create pulsating nebula effect with layered sine waves

    // Coordinate system centered at (0,0)
    vec2 st = uv * 10.0 - 5.0;

    // Time-based pulsation
    float time = sin(iTime * 0.5) * 0.5 + 0.5;

    // Layered sine waves with noise
    float wave1 = sin(st.x * 0.5 + iTime) * 0.3;
    float wave2 = sin(st.y * 0.5 + iTime * 0.7) * 0.3;
    float noise1 = turbulent(st * 0.5);
    float noise2 = turbulent(st * 0.7 + 100.0);

    // Combine waves and noise
    float pattern = wave1 + wave2 + noise1 * 0.2 + noise2 * 0.3;

    // Color transformation with time
    vec3 color = hsl(
        sin(iTime) * 0.5 + 0.5,   // Hue variation over time
        0.7 + pattern * 0.3,      // Saturation modulation
        0.5 + noise1 * 0.2       // Lightness modulation
    );

    // Add rotation to create dynamic movement
    float angle = iTime;
    vec2 rotated = vec2(
        st.x * cos(angle) - st.y * sin(angle),
        st.x * sin(angle) + st.y * cos(angle)
    );

    // Final color with pulsation effect
    vec3 finalColor = hsl2rgb(vec3(
        0.5 + pattern * 0.1,   // Base hue
        0.7 + noise1 * 0.3,    // Saturation
        0.3 + time * 0.7      // Pulsating lightness
    ));

    return vec4(finalColor, 1.0);
}
