vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    float angle = atan2(uv.y, uv.x);
    float radius = length(uv);

    // Create a moving tunnel effect by offsetting the angle with time
    angle += iTime * 0.5;

    // Add perspective projection for depth effect
    vec3 pos = vec3(
        cos(angle) * radius,
        sin(angle) * radius,
        radius * 2.0 + iTime * 0.1
    );

    // Add noise and turbulence to create organic patterns
    float noiseVal = turbulent(pos);
    float noise2D = turbulent(uv * 5.0 + vec2(iTime));

    // Create color transitions using HSL colors
    vec3 colorA = hsl(0.6, 1.0, 0.5);   // Blue-ish base color
    vec3 colorB = hsl(0.3, 1.0, 0.7);   // Greenish accent color

    // Mix colors based on noise values and create pulsating effect
    vec3 finalColor = mix(
        hsl2rgb(colorA),
        hsl2rgb(colorB),
        abs(noiseVal * 0.5 + 0.5)
    );

    // Add pulsating glow effect
    float brightness = sin(iTime) * 0.5 + 0.7;
    finalColor *= brightness;

    // Create depth fog effect
    float depthFog = smoothstep(1.0, 2.0, radius);
    finalColor = mix(finalColor, vec3(0.0), depthFog);

    return vec4(finalColor, 1.0);
}
