vec4 Effect2(vec2 uv) {
    // Create hexagonal grid coordinates
    vec2 q = uv * 10.0; // Scale up for more cells

    // Offset every other row to create hexagonal tiling
    vec2 offset = vec2(mod(q.y, 2.0), 0.0);
    vec2 pos = abs(q - offset) + vec2(0.5);

    // Calculate hexagon center and distance from center
    vec2 center = floor(pos) + vec2(0.5);
    vec2 toCenter = pos - center;
    float dist = length(toCenter);

    // Create pulsing effect using time and noise
    float hue = iTime * 0.1 + noise(center.xy * 0.1) * 0.5;
    hue += sin(iTime * 0.2 + pos.x * 0.3) * 0.3;

    // Convert to HSL color with pulsing hue
    vec3 hslColor = vec3(hue, 1.0, 0.5);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Add distance-based shading
    float shade = smoothstep(0.4, 0.6, dist);
    rgbColor *= mix(vec3(1.0), vec3(0.5), shade);

    return vec4(rgbColor, 1.0);
}
