vec4 Effect2(vec2 uv) {
    // Center and scale coordinates
    vec2 st = (uv - 0.5) * 12.0;

    // Add time-based rotation
    float t = iTime * 0.3;
    float ct = cos(t);
    float stt = sin(t);

    // Rotate coordinates
    vec2 rotated = vec2(
        st.x * ct - st.y * stt,
        st.x * stt + st.y * ct
    );

    // Create sine wave pattern with time offset
    float wave1 = sin(rotated.x + iTime) * 0.5;
    float wave2 = sin(rotated.y + iTime * 0.7);

    // Combine waves and add noise
    float value = (wave1 + wave2) * 0.5 + noise(vec3(st, iTime)) * 0.3;

    // Create pulsating color transitions using HSL
    vec3 hslColor = vec3(
        sin(iTime * 0.5 + value * 2.0) * 0.5 + 0.5,
        abs(sin(iTime * 0.3 + value)) * 0.7 + 0.3,
        0.6 + noise(vec3(st, iTime)) * 0.1
    );

    // Convert HSL to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
