vec4 Effect2(vec2 uv) {
    // Scale UV coordinates to -1.0 to 1.0 range
    vec2 st = uv * 2.0 - 1.0;

    // Create dynamic sine wave patterns with time offset
    float waveA = sin(st.x * 5.0 + iTime) * cos(st.y * 3.0 + iTime/2.0);
    float waveB = sin(st.x * 4.0 - iTime) * cos(st.y * 6.0 - iTime/2.0);

    // Add noise to create organic variations
    vec2 noiseSt = st * 5.0;
    float noiseA = noise(noiseSt + vec2(iTime, 0.0)) * 0.5;
    float noiseB = noise(noiseSt + vec2(-iTime, 0.0)) * 0.5;

    // Combine waves with noise
    waveA += noiseA;
    waveB += noiseB;

    // Create color transitions using HSL
    vec3 colorA = hsl(0.5 + sin(iTime) * 0.4, 1.0, 0.5);
    vec3 colorB = hsl(0.5 + cos(iTime) * 0.4, 1.0, 0.5);

    // Mix colors based on wave patterns
    vec3 finalColor = mix(colorA, colorB, pow(abs(waveA), 2.0));
    finalColor += mix(colorB, colorA, pow(abs(waveB), 2.0)) * 0.5;

    // Apply tonal adjustments
    finalColor *= 1.0 - abs(st.x) * 0.5;
    finalColor = clamp(finalColor, 0.0, 1.0);

    return vec4(finalColor, 1.0);
}
