//prompt: something wonderful
vec4 Effect2(vec2 uv) {
    // Create flowing patterns with time
    vec2 flow = uv + 0.5 * sin(uv * 10.0 + iTime);

    // Generate colorful interference pattern
    float patternX = sin(flow.x * 8.0 + iTime * 2.0);
    float patternY = cos(flow.y * 6.0 - iTime * 3.0);

    // Combine patterns with radial gradient
    vec2 center = uv - 0.5;
    float radius = length(center) * 10.0;

    // Create vibrant color transitions
    vec3 color = mix(
    vec3(0.4, 0.6, 1.0), // Blue-cyan
    vec3(1.0, 0.2, 0.8), // Magenta
    sin(patternX + patternY + radius) * 0.5 + 0.5
    );

    return vec4(color, step(0.95, abs(sin(iTime / 2.0))));
}
