vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for rotation
    vec2 polar = uv * 2.0 - 1.0;
    float radius = length(polar);
    float angle = atan(polar.y, polar.x) + iTime * 0.5;

    // Create radial gradient with pulsing effect
    float gradient = sin(radius * 8.0 + iTime) * 0.5 + 0.5;
    vec3 color = hsl2rgb(vec3(
        mod(angle * 0.1 + iTime, 1.0),  // Hue variation over time and angle
        0.7 + sin(radius * 4.0) * 0.3,   // Saturation variation with radius
        0.5 + gradient * 0.5           // Lightness variation with gradient
    ));

    // Add pulsating border effect
    float border = sin(angle * 2.0 + iTime * 2.0) * 0.1;
    color += vec3(border);

    return vec4(color, 1.0);
}
