vec4 Effect2(vec2 uv) {
    // Combine time and position with sine functions for color shifting
    float timeFactor = sin(iTime * 0.5 + uv.x * 10.0);
    vec3 colorShifts = vec3(
        sin(uv.x * pi + iTime),
        sin(uv.y * pi + iTime + 2.0),
        sin((uv.x + uv.y) * pi + iTime * 2.0)
    );

    // Add noise and turbulence for more dynamic effect
    vec2 noisePos = uv * 10.0;
    float noiseValue = turbulent(noisePos);

    // Combine everything into final color
    return vec4(
        sin(uv.x * pi + timeFactor) * 0.5 + 0.5 + noiseValue * 0.3,
        sin(uv.y * pi + timeFactor + 2.0) * 0.5 + 0.5 + noiseValue * 0.3,
        sin((uv.x + uv.y) * pi + timeFactor * 2.0) * 0.5 + 0.5 + noiseValue * 0.3,
        1.0
    );
}
