vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float angle = atan2(st.y, st.x);
    float radius = length(st);

    // Create rotating pattern by adding time-based rotation
    angle += iTime * 0.5;

    // Add pulsating effect using sine wave with increasing frequency
    radius *= sin(iTime * 3.0 + angle) * 0.5 + 1.0;

    // Use noise to create dynamic color shifts
    vec2 noiseUV = vec2(angle, radius);
    float noiseValue = noise(noiseUV * 10.0);

    // Create RGB shift effect using HSL colors
    vec3 hslColor = vec3(
        fract(iTime * 0.5 + noiseValue) * 360.0,
        0.7,
        0.5
    );
    vec3 rgbColor = hsl2rgb(hslColor);

    // Add pulsating intensity using turbulent noise
    float intensity = turbulent(vec3(uv * 10.0, iTime)) * 0.5 + 0.5;
    rgbColor *= intensity;

    return vec4(rgbColor, 1.0);
}
