vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x);
    // Create rotating sine wave pattern
    a += iTime * 0.5;
    float sa = sin(a + iTime) * 4.0;

    // Add noise and turbulence
    vec3 noise1 = vec3(noise(vec3(uv * 2.0, iTime)), 
                       noise(vec3(uv * 3.0, iTime)),
                       noise(vec3(uv * 4.0, iTime)));
    float turb = turbulent(vec2(a, r)) * 0.5 + 0.5;

    // Create color transitions
    vec3 color1 = hsl2rgb(vec3((sa + a) * 0.1, 0.7, 0.8));
    vec3 color2 = hsl2rgb(vec3(iTime * 0.2, 0.6, 0.9));

    // Combine patterns and colors
    float blend = sin(r * 5.0 + iTime) * 0.5 + 0.5;
    vec3 finalColor = mix(color1, color2, blend);
    finalColor *= turb;
    return vec4(finalColor, 1.0);
}
