vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan2(st.y, st.x);
    float r = length(st) * 0.5 + 0.5;
    // Create rotating color wheel effect
    theta += iTime * 0.5; // Rotate over time

    // Use HSL colors for smooth transitions
    vec3 hslColor;
    hslColor.x = (theta / (2.0 * PI) + 0.5) * 0.6; // Hue based on angle
    hslColor.y = 1.0; // Full saturation

    // Add noise and turbulence for dynamic variations
    float noiseValue = turbulent(vec3(r, iTime, 0.0)) * 0.2;
    hslColor.z = r + noiseValue; // Brightness varies with radius and noise

    // Convert HSL to RGB
    vec3 rgbColor = hsl2rgb(hslColor);

    // Add smooth transitions using another layer of noise
    float transitionNoise = abs(noise(vec2(r * 10.0, iTime))) * 0.5;
    rgbColor += transitionNoise;
    return vec4(rgbColor, 1.0);
}
