vec4 Effect2(vec2 uv) {
    // Scale and rotate coordinates
    vec2 st = uv * 10.0 + iTime;

    // Create rotating sine wave pattern
    float sinX = sin(st.x + iTime * 2.0);
    float sinY = sin(st.y + iTime * 3.0);
    float pattern = (sinX + sinY) * 0.5;

    // Add turbulence for dynamic effect
    vec2 noiseUV = uv * 100.0;
    float turb = turbulent(noiseUV);
    pattern += turb * 0.3;

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        fract(iTime * 0.1 + st.x * 0.2),
        0.7,
        0.5 + abs(pattern) * 0.5
    );
    vec3 rgbColor = hsl2rgb(hslColor);

    // Output the final color
    return vec4(rgbColor, 1.0);
}
