vec4 Effect2(vec2 uv) {
    // Create swirly pattern by manipulating UV coordinates
    vec2 center = vec2(0.5, 0.5);
    vec2 pos = uv - center;
    float radius = length(pos);
    float angle = atan2(pos.y, pos.x) + iTime * 1.5;

    // Add turbulence for dynamic patterns
    float turb = turbulent(vec3(uv * 4.0, iTime * 0.5)) * 0.5 + 0.5;
    radius *= 1.0 + turb * 0.2;

    // Create vibrant color gradients using HSL
    vec3 color = hsl2rgb(vec3(
        (angle * 0.5 + iTime) * 0.1,
        0.7 + sin(iTime * 0.5) * 0.3,
        0.8 + turb * 0.2
    ));

    // Add sine wave motion and RGB shifts
    float timeFactor = sin(iTime * 2.0 * pi);
    color.r += sin(angle * 2.0 + timeFactor * 2.0) * 0.3;
    color.g += sin(angle * 3.0 + timeFactor * 1.5) * 0.3;
    color.b += sin(angle * 4.0 + timeFactor * 1.0) * 0.3;

    // Combine everything for final output
    return vec4(color, 1.0);
}
