vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create square wave patterns
    vec2 st = uv * 8.0 - 4.0;
    st.x = sign(sin(st.x + time)) * 0.5 + 0.5;
    st.y = sign(sin(st.y * 2.0 - time)) * 0.5 + 0.5;

    // Add movement
    vec2 pos = uv * 4.0 + vec2(time, 0.0);
    float wave = sin(pos.x) + cos(pos.y);

    // Combine patterns with turbulence
    float pattern = (st.x + st.y) * 0.5;
    pattern += turbulent(uv * 16.0 + time) * 0.3;

    // HSV color transformation
    vec3 hsvColor = vec3(pattern * 2.0, 0.7, 0.5);
    vec3 rgbColor = hsv2rgb(hsvColor);

    return vec4(rgbColor, 1.0);
}
