vec4 Effect2(vec2 uv) {
    // Create flowing liquid effect using noise functions

    // Add time-based animation
    float t = iTime * 0.5;

    // Scale UV coordinates for better noise pattern
    vec2 st = uv * 3.0 + t;

    // Layered noise with different scales and speeds
    float n1 = noise(st);
    float n2 = noise(st * 0.7 + t * 0.5);
    float n3 = turbulent(vec3(st, t));

    // Combine noise layers
    float liquid = (n1 + n2 + n3) * 0.3;

    // Add color transformation using HSL to HSV conversion
    vec3 color = hsl2rgb(vec3(
        mod(liquid * 5.0 + t, 1.0),   // Hue variation
        0.7 + liquid * 0.3,          // Saturation
        0.5 + liquid * 0.5           // Brightness
    ));

    // Add turbulence for more detail
    vec2 uvTurb = uv + turbulent(uv * 10.0) * 0.1;
    float turb = turbulent(vec3(uvTurb, t)) * 0.3;

    // Final color with alpha
    return vec4(color + turb, 1.0);
}
