vec4 Effect2(vec2 uv) {
    // Create base frequency with time component
    float base_time = 0.5 * iTime;

    // Calculate color channels with different phase shifts
    vec3 color = vec3(
        sin(base_time + uv.x * 10.0 + uv.y * 5.0),
        sin(base_time + 2.0 + uv.x * 8.0 + uv.y * 6.0),
        sin(base_time + 4.0 + uv.x * 7.0 + uv.y * 9.0)
    );

    // Add noise to create more dynamic effect
    float noise_val = noise(vec2(uv.x * 5.0, uv.y * 5.0) + base_time);
    color += vec3(noise_val * 0.1);

    // Scale and offset to get values between 0-1
    color = (color * 0.5) + 0.5;

    return vec4(color, 1.0);
}
