vec4 Effect2(vec2 uv) {
    // Mouse position (assuming normalized coordinates from 0 to 1)
    vec2 mouse = vec2(0.5, 0.5); // Replace with actual mouse input

    // Time-based animation
    float time = iTime * 0.5;

    // Layered sine waves for plasma effect
    vec3 color = vec3(0.0);
    float freq = 1.0;
    for(int i=0; i<4; i++) {
        freq *= 2.0;
        color += sin(uv.x * freq + time) * 
                 sin(uv.y * freq + time) *
                 vec3(0.5, 0.7, 1.0);
    }

    // Add noise and turbulence
    vec2 st = uv * 8.0;
    float turb = turbulent(st + time);
    color += vec3(turb) * 0.5;

    // Mouse interaction
    vec2 mouseDir = uv - mouse;
    float dist = length(mouseDir);
    color *= sin(dist * 4.0 + time) * 0.5 + 0.5;

    // Color shift over time
    vec3 hsvColor = vec3(
        mod(time * 0.1, 1.0), 
        0.7,
        length(color) * 0.5 + 0.5
    );
    color = hsv2rgb(hsvColor);

    return vec4(color, 1.0);
}
