vec4 Effect2(vec2 uv) {
    // Flowing gradient base pattern with time-based transformation
    vec2 st = uv * 10.0 + vec2(sin(iTime), cos(iTime)) * 5.0;

    // Cellular noise pattern with time offset
    float n = noise(st * 0.5 + vec2(0.0, iTime * 0.5));
    n += noise(st * 0.25 + vec2(iTime, 0.0)) * 0.5;

    // Color transformation using HSL for vibrant colors
    vec3 color = hsl(
        sin(n * 180.0 + iTime) * 180.0,
        1.0,
        0.5 + turbulent(uv * 2.0 + iTime) * 0.5
    );

    // Add flowing gradient effect
    vec3 gradient = mix(
        hsl2rgb(vec3(60.0, 1.0, 0.5)),
        hsl2rgb(vec3(240.0, 1.0, 0.5)),
        uv.y
    );

    // Combine patterns with time-based modulation
    color = mix(color, gradient, sin(iTime) * 0.5 + 0.5);

    return vec4(color, 1.0);
}
