vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    float r = length(uv);
    float theta = atan2(uv.y, uv.x) * 6.0 + iTime * 0.5;

    // Create hexagonal pattern using sine function
    float d = 1.0 / (r * cos(theta));

    // Add noise and turbulence for dynamic effect
    vec3 noiseVal = vec3(noise(uv * 2.0 + sin(iTime)));
    float turb = turbulent(uv * 4.0 + iTime);

    // Create pulsating color using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(
        sin(r * 5.0 + theta) * 0.5 + 0.5,
        1.0 - abs(sin(iTime * 0.5)),
        0.5 + 0.5 * sin(iTime * 0.5)
    ));

    // Combine all elements for final effect
    return vec4(color * (d + noiseVal.x) * turb, 1.0);
}
