vec4 Effect2(vec2 uv) {
    // Scale UV coordinates for a grid effect
    vec2 st = uv * 10.0;

    // Get integer grid position
    vec2 pos = floor(st);

    // Calculate time-based pulsing factor
    float t = iTime * 0.5;

    // Add noise to create organic movement
    float noiseVal = noise(pos + t) * 0.5 + 0.5;

    // Create pulsing effect using sine wave and noise
    float pulse = sin(t + pos.x + pos.y) * noiseVal;

    // Calculate color based on position and time
    vec3 baseColor = hsl2rgb(vec3(pos.x * 0.1, pos.y * 0.1, 0.5));

    // Add pulsing effect to color
    vec3 finalColor = baseColor * (0.5 + 0.5 * sin(t + pulse));

    // Add turbulence for extra detail
    float turb = turbulent(vec2(st) + t);
    finalColor *= 1.0 + turb * 0.2;

    return vec4(finalColor, 1.0);
}
