vec4 Effect2(vec2 uv) {
    vec2 st = uv - 0.5;
    float d = length(st);

    // Create circular pattern with time-based animation
    float f = sin(iTime * 0.5 + d * 10.0) * cos(iTime * 0.7 + d * 5.0);

    // Add noise and turbulence for more complex patterns
    vec2 noiseScale = st * 50.0;
    float turb = turbulent(noiseScale);

    // Mix colors with smooth transitions
    vec3 color1 = hsl2rgb(vec3(0.5 + sin(iTime) * 0.4, 0.7, 0.6));
    vec3 color2 = hsl2rgb(vec3(0.2 + cos(iTime) * 0.4, 0.8, 0.5));

    // Combine patterns and colors
    float mixVal = smoothstep(0.3, 0.7, d);
    vec3 finalColor = mix(color1, color2, mixVal);

    // Add turbulence effect
    finalColor *= 1.0 + turb * 0.5;

    return vec4(finalColor, 1.0);
}
