vec4 Effect2(vec2 uv) {
    // Center the coordinates and convert to polar
    vec2 centered = uv - 0.5;
    float r = length(centered);
    float theta = atan2(centered.y, centered.x) + iTime * 0.5; // Rotate over time

    // Create color wheel effect with pulsing
    float hue = theta / (2.0 * PI) + sin(iTime * 0.7) * 0.3;
    float saturation = r * 0.8 + 0.2;
    float lightness = 0.5 + turbulent(vec2(r, iTime * 0.1)) * 0.3;

    // Add pulsing effect to radius
    float pulse = sin(iTime) * 0.3;
    r = min(1.0, r + pulse);

    // Create radial gradient with noise
    vec3 color = hsl2rgb(vec3(hue, saturation, lightness));
    color *= 1.0 - r * 0.7; // Darken towards edges

    return vec4(color, 1.0);
}
