vec4 Effect2(vec2 uv) {
    // Rotate coordinates based on time
    float angle = sin(iTime * 0.5 + pi) * 0.5 + 0.5;
    vec2 q = uv - 0.5;
    q = vec2(q.x * cos(angle) - q.y * sin(angle),
             q.x * sin(angle) + q.y * cos(angle)) + 0.5;
    // Create sine wave patterns at different scales
    float wave1 = sin(q.x * 20.0 + iTime) * 0.5 + 0.5;
    float wave2 = cos(q.y * 20.0 + iTime * 0.5) * 0.5 + 0.5;
    float wave3 = sin((q.x + q.y) * 15.0 + iTime * 0.7);

    // Combine waves with noise and turbulence
    float pattern = (wave1 + wave2 + wave3) / 3.0;
    pattern += noise(q * 4.0 + iTime) * 0.3;
    pattern += turbulent(q * 8.0 + iTime) * 0.2;
    // Convert to HSL color space
    vec3 hslColor = vec3(
        fract(iTime / 3.0 + pattern * 0.5),  // Hue
        0.8,                                  // Saturation
        mix(0.4, 0.7, sin(pattern * pi) * 0.5 + 0.5)  // Lightness
    );
    // Convert HSL to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
