vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);
    float time = iTime * 0.5;

    // Generate particle positions using noise
    for(float i = 0.0; i < 100.0; i++) {
        vec2 pos = vec2(
            fract(sin(i + time) * 374.54),
            fract(cos(i + time) * 612.38)
        );

        // Add noise to positions
        pos += vec2(noise(vec3(pos, time)), noise(vec3(pos.yx, time)));

        // Calculate distance from uv to particle position
        float dist = length(uv - (pos * 0.5 + 0.5));

        // Create glowing effect using turbulence
        float glow = exp(-dist * 12.0) * 
                    turbulent(vec3(pos, time)) * 
                    sin(time + i);

        // Add color with shifting hues
        vec3 particleColor = hsl2rgb(vec3(
            fract(time / 3.0 + i * 0.1),
            0.8,
            0.5
        ));

        color += particleColor * glow;
    }

    return vec4(color, 1.0);
}
