vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Create rotating circular patterns using polar coordinates
    float r = length(uv);
    float theta = atan2(uv.y, uv.x) + iTime * 0.5;

    // Add noise and turbulence to create dynamic shapes
    float pattern1 = sin(theta + iTime) * r;
    float pattern2 = turbulent(vec3(uv * 4.0, iTime)) * 0.5 + 0.5;

    // Create gradient overlays using HSL colors
    vec3 color1 = hsl(r * 0.7 + iTime * 0.2, 0.8, 0.6);
    vec3 color2 = hsl(theta * 0.4 + iTime * 0.3, 0.7, 0.5);

    // Combine patterns and colors
    float blend = min(pattern1, pattern2);
    color = mix(color1, color2, blend);

    // Add noise overlay for extra detail
    color *= hsl2rgb(vec3(noise(uv * 8.0 + iTime) * 0.5 + 0.5, 1.0, 1.0));

    // Brightness and contrast adjustments
    color = (color - 0.5) * 1.5 + 0.5;

    return vec4(color, 1.0);
}
