vec4 Effect2(vec2 uv) {
    // Scale UV coordinates to range [-1, 1]
    vec2 st = uv * 2.0 - 1.0;

    // Create a time-based wave pattern
    float time = iTime * 0.5;
    float wave1 = sin(st.x * 10.0 + time) * 0.5;
    float wave2 = cos(st.y * 10.0 - time) * 0.3;

    // Combine waves with turbulence
    float noiseValue = turbulent(vec2(st.x * 5.0, st.y * 5.0)) * 0.5 + 0.5;
    float pattern = wave1 + wave2 + noiseValue;

    // Create RGB shifts using HSL colors
    vec3 color = hsl(
        mod((st.x * 0.5 + time/2.0), 1.0),
        0.7,
        0.6
    );

    // Convert to RGB and mix with pattern
    vec3 rgbColor = hsl2rgb(color);
    float finalPattern = (sin(pattern * 4.0) + 1.0) * 0.5;

    // Combine everything for the final effect
    vec3 finalColor = rgbColor * finalPattern;

    return vec4(finalColor, 1.0);
}
