vec4 Effect2(vec2 uv) {
    // Scale UV coordinates to range [-1, 1]
    vec2 st = uv * 2.0 - 1.0;

    // Create a time-varying sine wave pattern
    float waveX = sin(st.x * 5.0 + iTime) * 0.5 + 0.5;
    float waveY = sin(st.y * 5.0 + iTime * 0.5) * 0.5 + 0.5;

    // Combine waves and add noise
    float value = (waveX + waveY) * 0.5;
    value += noise(st * 10.0 + vec2(iTime)) * 0.3;

    // Create shifting RGB colors using HSL conversion
    vec3 color = hsl(value * 360.0, 1.0, 0.5);

    return vec4(color, 1.0);
}
