//prompt: a spiral
vec4 Effect2(vec2 uv) {
    // Center the coordinates and scale to [-1, 1] range
    vec2 centeredUV = uv * 2.0 - 1.0;

    // Calculate polar coordinates
    float r = length(centeredUV);
    float theta = atan(centeredUV.y, centeredUV.x);

    // Spiral equation: r = a + b*theta
    float a = 0.5;
    float b = 0.2;
    float spiralR = a + b * (theta + iTime); // Add time for animation

    // Create pattern based on distance from spiral
    float pattern = abs(sin(r * 5.0 - theta * 3.0));

    // Color the spiral
    vec3 color = mix(
    vec3(1.0, 0.2, 0.3), // Pink for one side of the spiral
    vec3(0.2, 0.8, 1.0), // Blue for the other side
    step(0.5, pattern)
    );

    return vec4(color, 1.0);
}
