vec4 Effect2(vec2 uv) {
    // Create a time-varying base pattern using noise
    float time = iTime * 0.5; // Scale time for slower animation

    // Combine UV coordinates with time to create movement
    vec2 st = uv * 10.0 + vec2(time, time); // Scale and offset coordinates

    // Use noise functions to create a flowing pattern
    float n1 = noise(st);
    float n2 = noise(vec3(st.x, st.y, time)); // Add time as third dimension

    // Mix the two noise values with some scaling
    float mixVal = (n1 + n2 * 0.5) * 0.5 + 0.5; // Scale to 0-1 range

    // Create color shifts using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(
        mixVal * 360.0, // Hue varies with noise
        0.7,            // Saturation
        0.5             // Lightness
    ));

    // Add turbulence for more dynamic patterns
    float turb = turbulent(st) + turbulent(vec2(time)) * 0.5;
    color *= 1.0 + turb * 0.3; // Scale and add to color

    return vec4(color, 1.0);
}
