vec4 Effect2(vec2 uv) {
    vec3 pos = vec3(uv * 2.0 - 1.0, 0.0);
    pos.xy *= 0.5;

    float time = iTime * 0.5;

    // Generate base noise patterns
    float n1 = noise(pos + vec3(time, 0.0, 0.0)) * 0.5 + 0.5;
    float n2 = noise(pos + vec3(0.0, time, 0.0)) * 0.5 + 0.5;

    // Create leaf glow effect
    vec3 color = hsl(n1 * 0.6 + 0.3, 0.8, 0.7);
    color *= n2;

    // Add turbulence for dynamic movement
    float turb = turbulent(pos + vec3(time)) * 0.5 + 0.5;
    color *= turb;

    // Create shadow effect
    float shadow = noise(vec2(n1 * 10.0, time)) * 0.5 + 0.5;
    color *= mix(1.0, 0.3, shadow);

    // Add ambient glow
    vec3 ambient = hsl(0.6, 0.4, 0.2) * (n1 * 0.5 + 0.5);
    color += ambient;

    return vec4(color, 1.0);
}
