vec4 Effect2(vec2 uv) {
    // Scale UV coordinates for tessellation pattern
    vec2 st = uv * 10.0;

    // Add noise and time-based variation
    float n = noise(st + iTime);
    vec2 offset = vec2(sin(iTime * 0.5), cos(iTime)) * 0.3;

    // Create shifting grid pattern
    vec2 grid = fract(st + offset) - 0.5;
    float d = length(grid);

    // Add turbulence for more dynamic effect
    float t = turbulent(st * 0.5 + iTime);

    // Combine patterns and create color transitions
    vec3 color = hsl2rgb(vec3(
        mod(length(st) * 0.1 + iTime, 1.0), 
        0.7,
        0.5 + t * 0.5
    ));

    // Mix colors based on distance and noise
    color *= mix(0.8, 1.0, smoothstep(0.4, 0.6, d));
    color += n * 0.2;

    return vec4(color, 1.0);
}
