vec4 Effect2(vec2 uv) {
    // Base color with time-based hue shift
    vec3 baseColor = hsl2rgb(vec3(0.5 + 0.5*sin(iTime*0.5), 1.0, 0.5));

    // Create a moving pattern using noise and turbulence
    float noiseScale = 4.0;
    vec2 noiseUV = uv * noiseScale + iTime;
    float noiseValue = noise(noiseUV);

    // Add turbulent trails effect
    vec3 trailColor = hsl2rgb(vec3(0.5 + 0.5*sin(iTime*0.3), 1.0, 0.7));
    float trailStrength = turbulent(uv * 4.0 + iTime) * 0.5;

    // Combine colors with noise modulation
    vec3 finalColor = baseColor * (1.0 - noiseValue) + trailColor * noiseValue;
    finalColor += trailStrength * 0.5;

    // Add glow effect based on distance from center
    float dist = length(uv);
    float glow = smoothstep(0.8, 1.2, 1.0 - dist);
    finalColor *= 1.0 + glow * 0.5;

    return vec4(finalColor, 1.0);
}
