vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Create rotating sine wave pattern
    float speed = 0.5;
    theta += iTime * speed;

    // Calculate sine wave with time-based rotation
    float wave = sin(theta + iTime) * 0.5 + 0.5;
    wave *= sin(r * 10.0 - iTime * 2.0);
    // Create color transitions using HSL
    vec3 hslColor = vec3(
        theta * 0.15 + iTime * 0.1,   // Hue
        0.7 + wave * 0.3,             // Saturation
        0.5 + sin(iTime) * 0.2        // Lightness
    );

    // Convert HSL to RGB and mix with noise
    vec3 color = hsl2rgb(hslColor);
    float turbulence = turbulent(uv * 10.0 + iTime);
    color *= 0.5 + turbulence * 0.5;
    return vec4(color, 1.0);
}
