//prompt: something Wonderful!
vec4 Effect2(vec2 uv) {
    // Create expanding rings effect with color variations
    vec2 pos = uv - 0.5;
    float r = length(pos);
    r += iTime * 0.1;

    // Add noise and sine patterns for detail
    float s = sin(r * 3. + iTime) * 0.5 + 0.5;
    vec2 st = uv * 8.; 
    float f = noise(st + iTime);

    // Create RGB values with time-based variations
    vec3 col = vec3(
        (sin(r * 4. + iTime) * 0.5 + 0.5) + f * 0.2,
        (sin(r * 6. - iTime*2.) * 0.5 + 0.5) + f * 0.2,
        sin(r * 8. + iTime*3.) * 0.5 + 0.5 + f * 0.2
    );

    // Add noise to each channel for texture
    col += vec3(noise(uv + iTime), noise(uv + iTime + 1.), noise(uv + iTime - 1.)) * 0.3;

    return vec4(col, 1.0);
}
