vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create rotating patterns with multiple frequencies
    vec2 st = uv * 3.0 + time;
    float angle1 = sin(time * 2.0) * 0.5 + 0.5;
    float angle2 = cos(time * 1.5) * 0.5 + 0.5;

    // Layer 1: Rotating squares
    vec2 rotatedUv1 = uv * mat2(cos(angle1), -sin(angle1), sin(angle1), cos(angle1)) * 0.8;
    float pattern1 = sin(rotatedUv1.x * 4.0) + sin(rotatedUv1.y * 4.0);

    // Layer 2: Rotating circles
    vec2 rotatedUv2 = uv * mat2(cos(angle2), -sin(angle2), sin(angle2), cos(angle2)) * 0.8;
    float dist = length(rotatedUv2) * 3.0;
    float pattern2 = sin(dist + time * 2.0);

    // Combine patterns with noise
    float combined = (pattern1 * 0.5 + pattern2 * 0.25) + turbulent(uv * 4.0 + time);

    // Create color transitions using HSL
    vec3 color = hsl2rgb(vec3(
        mod(time * 0.2 + uv.x * 2.0, 1.0),
        0.7,
        0.5 + combined * 0.2
    ));

    return vec4(color, 1.0);
}
